      />

     {value !== null && (
        <Box sx={{ ml: 1 }}>{labels[hover !== -1 ? hover : value]}</Box>
      )}
      <Button disabled = {loading} variant = 'outlined' style={{color : 'white', marginLeft : '20px', backgroundColor : 'red'}} onClick={giveRating}>Submit</Button>
