// Import the functions you need from the SDKs you need
import { initializeApp } from "firebase/app";
import {getAuth, createUserWithEmailAndPassword, onAuthStateChanged, signOut, signInWithEmailAndPassword} from 'firebase/auth';
import { useEffect, useState } from "react";

// TODO: Add SDKs for Firebase products that you want to use
// https://firebase.google.com/docs/web/setup#available-libraries

// Your web app's Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyD-D6no--shaMGBARccTXmMcScmCVr0DwQ",
  authDomain: "loginmoiveapp.firebaseapp.com",
  projectId: "loginmoiveapp",
  storageBucket: "loginmoiveapp.appspot.com",
  messagingSenderId: "282173325830",
  appId: "1:282173325830:web:b8ea765cdfe89ef3113987"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);

const auth = getAuth();   // Authetication module

export const Signup = (email, password)=>{
    createUserWithEmailAndPassword(auth,email,password);  //Creating signup function using auth module

}

export const Login = (email, password)=>{
    return signInWithEmailAndPassword(auth,email,password);  //Creating signup function using auth module

}

export function logout(){
   return signOut(auth);
}

//custom hook

export const useAuth = ()=>{
    const [currentUser, setCurrentuser] = useState();

    useEffect(()=>{
       const unsub =  onAuthStateChanged(auth, user=> setCurrentuser(user));
       return unsub;
    },[])

    return currentUser;


}
