import * as React from 'react';
import Rating from '@mui/material/Rating';
import Box from '@mui/material/Box';
import StarIcon from '@mui/icons-material/Star';
// import { Button } from '@mui/material';
import { db } from './firebase';
import {collection, addDoc} from 'firebase/firestore';
import { useStyles } from './Style';




const labels = {
  0.5: 'Useless',
  1: 'Useless+',
  1.5: 'Poor',
  2: 'Poor+',
  2.5: 'Ok',
  3: 'Ok+',
  3.5: 'Good',
  4: 'Good+',
  4.5: 'Excellent',
  5: 'Excellent+',
};

export default function HoverRating() {
  const [value, setValue] = React.useState(2);
  const [hover, setHover] = React.useState(-1);


  const [rate,setRate] = React.useState('');

  const userCollectionRef = collection(db,"Rating")


  const giveRating = async()=>{
      await addDoc(userCollectionRef,{UserId : '02', MovieId : '200', Rating : hover})
  }

  return (
      <>

    <Box
      sx={{
        width: 200,
        display: 'flex',
        alignItems: 'center',
        color : 'white',
      }}
    >



      <Rating
        name="hover-feedback"
        value={value}
        precision={0.5}
        onChange={(event, newValue) => {
          setValue(newValue);

        }}
        onChangeActive={(event, newHover) => {
          setHover(newHover);
        }}

        emptyIcon={<StarIcon style={{ opacity: 0.55 }} fontSize="inherit" />}

      />

     {value !== null && (
        <Box sx={{ ml: 1 }}>{labels[hover !== -1 ? hover : value]}</Box>
      )}

    </Box>
    <button onClick={giveRating}>Submit</button>
    {/* <input type = "text" placeholder = "review"
    onChange = {(e)=>{setRate(e.target.value)}}
  /> */}
    </>
  );
}
