import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { NavLink } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { loadPostSuccess } from './actions';








export default function ImgMediaCard(props) {

const dispatch = useDispatch();


  return (
    <>
  <div style = {{backgroundColor : 'black', border : 'red'}}>

  {/* <NavLink to = {`/movie/${props.movie.imdbID}`} style = {{textDecoration : 'none'}}> */}

    <Card sx={{ maxWidth: 250 , padding : '30px', margin : '10px', border : 'solid', backgroundColor : 'black'}} >
      <CardMedia
        component="img"
        alt="green iguana"
        height="250"
        image={props.image}
      />
      <CardContent style = {{maxHeight : '70px'}}>
      <div>
        <Typography align="center" gutterBottom variant='subtitle1' component="div" fontSize = "17px" color = 'white'>
          {props.title}
        </Typography>
        </div>
      </CardContent>
      <CardActions>

        <NavLink to = {`/movie/${props.movie.imdbID}/${props.movie.Title}`} style={{textDecoration : 'none'}}><Button variant="contained" size= 'small' color='error'
        // onClick ={()=> props.onMovieSelect(props.movie.imdbID)}
        onClick ={()=> (props.onMovieSelect(props.id))}
        >WATCH NOW</Button></NavLink>

        <Button variant='outlined' size="small" style ={{color : 'blue', borderColor : 'gold'}}>ADD TO WISHLIST</Button>
      </CardActions>
    </Card>

    </div>
    </>
  );
}
