    NEPFLIX
  </Typography>
  </NavLink>
  <Button className={classes.buttop} variant = "contained" color = "error">Sign In</Button>
  </div></div> 
  </div>


<br/>



    <Grid  container border = "solid" height = "70vh" width = "100%" spacing = {1}>
        <Grid item md={3}>
        <Box border = "solid" className={classes.detailimage}>

        {/* <img src = {'https://image.tmdb.org/t/p/w185_and_h278_bestv2/VfjxxO3trUCWWUtHMmjRsE1i8W.jpg'} alt = "asd" className ={classes.img}/> */}
        <img src = {movieInfo?.Poster} alt = "asd" className ={classes.img}/>
</Box>
        </Grid>

        <Grid item md={9} spacing = {6}>
        <div className = {classes.detailsdiv}>
        <Box border = "solid">
<h1>{movieInfo?.Title}</h1>
</Box>
<br/>

<Box border = "solid">
<h1>{movieInfo.Released}</h1>
</Box>

<Box border = "solid">
<h1>Rating </h1>
</Box>

<Box border = "solid">
<h1>Overview</h1>
</Box>

<br/>
<br/>

<Box border = "solid">
<h1>Casts</h1>

<ImageAvatars/>

</Box>

</div>
        </Grid>


    </Grid>





