import * as React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { useParams } from 'react-router';


import { db, useAuth } from './firebase';
import {collection, addDoc, getDocs,doc,updateDoc} from 'firebase/firestore';
import { typography } from '@mui/system';
import { Typography } from '@mui/material';



function createData(Username, Ratings ) {
  return { Username, Ratings };
}

// const rows = [
//   createData('Suzit Paudel', 'Good'),
//   createData('Subrat Regmi', 'Excellent'),
//   createData('Rajesh Rai', 'Bad'),
//   createData('Sujan Khadka', 'Poor'),
//   createData('Sagar Thapa', 'Ok'),
// ];

export default function BasicTable() {

    const {id} = useParams();

    const [users ,setUsers]= React.useState([]); 

    const usersCollectionRef = collection(db, "Rating")   //collecting datas from database from firebase to display 

    React.useEffect(()=>{

        const getUsers = async()=>{
            const data = await getDocs(usersCollectionRef)
            setUsers(data.docs.map((docs)=>({...docs.data(),id : docs.id})))



        }
        getUsers();
        },[]);


  return (
<>

  <div>
      <Typography variant = 'h3' align = 'center'>Movie Ratings</Typography>
  </div>
    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 1000 }} aria-label="simple table">
        <TableHead>
          <TableRow >
            <TableCell style = {{color : 'red'}}>USERNAME</TableCell>
            <TableCell align="right" style = {{color : 'red'}}>RATING</TableCell>

          </TableRow>
        </TableHead>
        <TableBody>

          {users.filter(row => row.MovieId == id ).map((row,ind) => (

            <TableRow
              key={row.MovieId}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >

              <TableCell component="th" scope="row">
                {row.UserId}
              </TableCell>
              <TableCell align="right">{row.Rating}</TableCell>
              <TableCell align="right">{row.MovieId}</TableCell>

            </TableRow> 

          ))}
        </TableBody>



      </Table>
    </TableContainer> 

    </>
  );
}
