    // backgroundColor : 'black',
    fontSize: "50px",
    textAlign: "center",
    textDecoration: "underline",
    fontFamily: "",
    borderRadius: "40px",
  },

  detailsmaindiv: {
    // backgroundColor : 'black',
    // backgroundImage: "url(images/bg.jpeg)",
    // height : '100vh',

    height: "600px",
  },

  subscription: {
    fontSize: "25px",
    color: "white",
    textAlign: "center",
    backgroundColor: "#B22222",
  },

  // wallpaper : {

  //     // backgroundImage: "url(images/nfwallpaper.jpeg)"

