  //     backgroundColor : 'black',
  //     // backgroundColor: 'rgba(0,0,0,0.5)'

  // },

  footer: {
    textAlign: "center",
    backgroundColor: "black",
    marginTop: "24vh",
    color: "white",
    // fontFamily: 'sans-serif'
    fontFamily: "Roboto",
  },

  // main : {
  //     marginTop : '100px'
  // },

  detailsdiv: {
    fontFamily: "Scheherazade New",
    fontSize: "13px",
    color: "white",
