    width: "100%",
  },

  wlbutton: {
    height: "50px",
    width: "250px",
  },

  box: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "space-between",
  },

  form: {
    marginTop: "50px",
    display: "flex",
    justifyContent: "center",
  },

  header: {
    marginTop: "0px",
    marginBottom: "40px",
    display: "flex",
    justifyContent: "space-between",
  },

  input: {
    width: "50%",
  },
