import React from "react";
import { useStyles } from "./Style";
import { Typography, Grid } from "@mui/material";
// import { NavLink } from "react-router-dom";
import { Box } from "@mui/system";
// import ImageAvatars from "./Avatar";
import axios from 'axios';
import { useState, useEffect } from 'react';

// import { Box } from "@mui/system";


const key = '900505d8';
const Details = (props)=>{
    const classes = useStyles();


const [movieInfo, setMovieInfo] = useState();
  const { selectedMovie } = props;

  useEffect(() => {
    axios.get(
      `https://www.omdbapi.com/?i=${selectedMovie}&apikey=${key}`
    ).then((response) => setMovieInfo(response.data));
  }, [selectedMovie]);




    return (
        <>
<br/>
<hr/>
 <div className={classes.main}>
  <div>
  <div className={classes.header}>
  <Typography variant="h2" color ="blue">
    MOVIE DETAILS
  </Typography>
  </div></div> 
  </div>


<br/>



    <Grid  container border = "solid" height = "70vh" width = "100%" spacing = {1}>
        <Grid item md={3}>
        <Box border = "solid" className={classes.detailimage}>

        {/* <img src = {'https://image.tmdb.org/t/p/w185_and_h278_bestv2/VfjxxO3trUCWWUtHMmjRsE1i8W.jpg'} alt = "asd" className ={classes.img}/> */}
        <img src = {movieInfo?.Poster} alt = "asd" className ={classes.img}/>
</Box>
        </Grid>

        <Grid item md={9} spacing = {6}>
        <div className = {classes.detailsdiv}>
        <Box border = "solid">
<h1>{movieInfo?.Title}</h1>
</Box>
<br/>

<Box border = "solid">
<h1>{movieInfo?.Released}</h1>
</Box>

<Box border = "solid">
<h1>{movieInfo?.Genre}</h1>
</Box>

<Box border = "solid">
<h1>{movieInfo?.Plot}</h1>
</Box>

<Box border = "solid">
<h1>Casts</h1>
<h3>{movieInfo?.Actors}</h3>
{/* <ImageAvatars/> */}

</Box>

</div>
        </Grid>


    </Grid>





