<Grid item md={6} >
<Box>
    <h1 style={{textAlign : "center"}}>box</h1>
</Box>
</Grid>
<Grid item md={6} >
<Box>
    <h1 style={{textAlign : "center"}}>box</h1>
</Box>
</Grid>
</Grid> */}

<footer className = {classes.footer}>All Rights Reserved @2021</footer>


        </>
    );
}

export default Details;
